mapper.select:
SELECT id, category, attraction_name AS attractionName,
address, attraction_image_url AS attractionImageUrl
FROM attraction
LEFT OUTER JOIN (
SELECT attraction_id, COUNT(*) AS reviewsCount
FROM attraction_review
GROUP BY attraction_id
) x ON attraction.id = x.attraction_id

WHERE category = #{category}

        AND sido_code = #{sidoCode}


        AND gugun_code = #{gugunCode}


        AND attraction_name LIKE CONCAT('%', #{title}, '%')

LIMIT #{limit} OFFSET #{offset}
